import React, { useState } from "react";
// Heroicons SVGs for arrows
const ArrowLeft = () => (
  <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={2} stroke="currentColor" className="w-8 h-8">
    <path strokeLinecap="round" strokeLinejoin="round" d="M15 19l-7-7 7-7" />
  </svg>
);
const ArrowRight = () => (
  <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={2} stroke="currentColor" className="w-8 h-8">
    <path strokeLinecap="round" strokeLinejoin="round" d="M9 5l7 7-7 7" />
  </svg>
);

interface CategoryGalleryProps {
  categories: Array<{
    category: string;
    images: string[];
  }>;
}


const CategoryGallery: React.FC<CategoryGalleryProps> = ({ categories }) => {
  const [selectedCategory, setSelectedCategory] = useState(categories[0]?.category || "");
  const [modalImage, setModalImage] = useState<string | null>(null);
  const [modalIndex, setModalIndex] = useState<number | null>(null);

  const currentCategory = categories.find(cat => cat.category === selectedCategory);

  const openModal = (img: string, idx: number) => {
    setModalImage(img);
    setModalIndex(idx);
  };

  const closeModal = () => {
    setModalImage(null);
    setModalIndex(null);
  };

  const showPrev = () => {
    if (modalIndex !== null && currentCategory) {
      const prevIdx = (modalIndex - 1 + currentCategory.images.length) % currentCategory.images.length;
      setModalImage(currentCategory.images[prevIdx]);
      setModalIndex(prevIdx);
    }
  };

  const showNext = () => {
    if (modalIndex !== null && currentCategory) {
      const nextIdx = (modalIndex + 1) % currentCategory.images.length;
      setModalImage(currentCategory.images[nextIdx]);
      setModalIndex(nextIdx);
    }
  };

  return (
    <div>
      <div className="flex flex-wrap gap-2 mb-6 justify-center">
        {categories.map(cat => (
          <button
            key={cat.category}
            className={`px-4 py-2 rounded-md border transition-colors text-sm font-medium
              ${selectedCategory === cat.category
                ? "bg-orange-600 text-white border-orange-600 shadow"
                : "bg-transparent border border-border text-foreground dark:text-white hover:bg-muted dark:hover:bg-muted"}
            `}
            onClick={() => setSelectedCategory(cat.category)}
          >
            {cat.category}
          </button>
        ))}
      </div>
      <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
      {currentCategory?.images.map((img, idx) => (
        <button
        key={idx}
        className="focus:outline-none"
        onClick={() => openModal(img, idx)}
        style={{ background: "none", border: "none", padding: 0, margin: 0 }}
        >
        <img
          src={img}
          alt={`${selectedCategory} ${idx + 1}`}
          className="h-40 w-full object-cover rounded shadow hover:ring-2 hover:ring-orange-600 transition"
        />
        </button>
      ))}
      </div>

      {/* Modal for large image */}
      {modalImage && modalIndex !== null && (
        <div
          className="fixed inset-0 z-50 flex items-center justify-center bg-black bg-opacity-70 backdrop-blur-md"
          onClick={closeModal}
        >
          <button
            className="absolute left-8 top-1/2 transform -translate-y-1/2 text-white px-2 py-2 bg-black bg-opacity-40 rounded-full hover:bg-opacity-70 flex items-center justify-center shadow-lg"
            onClick={e => { e.stopPropagation(); showPrev(); }}
            aria-label="Previous"
          >
            <ArrowLeft />
          </button>
          <img
            src={modalImage}
            alt="Large preview"
            className="max-h-[80vh] max-w-[90vw] rounded shadow-lg border-4 border-white"
            onClick={e => e.stopPropagation()}
          />
          <button
            className="absolute right-8 top-1/2 transform -translate-y-1/2 text-white px-2 py-2 bg-black bg-opacity-40 rounded-full hover:bg-opacity-70 flex items-center justify-center shadow-lg"
            onClick={e => { e.stopPropagation(); showNext(); }}
            aria-label="Next"
          >
            <ArrowRight />
          </button>
          <button
            className="absolute top-6 right-8 text-white text-3xl font-bold"
            onClick={e => { e.stopPropagation(); closeModal(); }}
            aria-label="Close"
          >
            &times;
          </button>
        </div>
      )}
    </div>
  );
};

export default CategoryGallery;

client/src/components/GuestSelectionPopo.content:
import { useState } from "react";
import {
  Popover,
  PopoverContent,
  PopoverTrigger,
} from "@/components/ui/popover";
import { Button } from "@/components/ui/button";
import { Label } from "@/components/ui/label";
import { Input } from "@/components/ui/input";
import { Users } from "lucide-react";

interface GuestSelectionPopoverProps {
  adults: number;
  children: number;
  maxGuests: number;
  onAdultsChange: (value: number) => void;
  onChildrenChange: (value: number) => void;
}

export default function GuestSelectionPopover({
  adults,
  children,
  maxGuests,
  onAdultsChange,
  onChildrenChange,
}: GuestSelectionPopoverProps) {
  const totalGuests = adults + children;

  const handleAdultsChange = (increment: number) => {
    const newValue = adults + increment;
    if (newValue >= 1 && newValue + children <= maxGuests) {
      onAdultsChange(newValue);
    }
  };

  const handleChildrenChange = (increment: number) => {
    const newValue = children + increment;
    if (newValue >= 0 && adults + newValue <= maxGuests) {
      onChildrenChange(newValue);
    }
  };

  return (
    <Popover>
      <PopoverTrigger asChild>
        <Button variant="outline" className="w-full justify-start text-left font-normal">
          <Users className="mr-2 h-4 w-4" />
          {totalGuests} Guest{totalGuests !== 1 && "s"}
        </Button>
      </PopoverTrigger>
      <PopoverContent className="w-80">
        <div className="grid gap-4">
          <div className="space-y-2">
            <h4 className="font-medium leading-none">Guests</h4>
            <p className="text-sm text-muted-foreground">
              Select the number of adults and children.
            </p>
          </div>
          <div className="grid gap-2">
            <div className="flex items-center justify-between">
              <div>
                <Label htmlFor="adults">Adults</Label>
                <p className="text-sm text-muted-foreground">Ages 13 or more</p>
              </div>
              <div className="flex items-center gap-2">
                <Button
                  variant="outline"
                  size="icon"
                  className="h-8 w-8"
                  onClick={() => handleAdultsChange(-1)}
                  disabled={adults <= 1}
                >
                  -
                </Button>
                <Input
                  id="adults"
                  type="number"
                  className="w-12 text-center"
                  value={adults}
                  readOnly
                />
                <Button
                  variant="outline"
                  size="icon"
                  className="h-8 w-8"
                  onClick={() => handleAdultsChange(1)}
                  disabled={totalGuests >= maxGuests}
                >
                  +
                </Button>
              </div>
            </div>
            <div className="flex items-center justify-between">
              <div>
                <Label htmlFor="children">Children</Label>
                <p className="text-sm text-muted-foreground">Ages 2 - 12</p>
              </div>
              <div className="flex items-center gap-2">
                <Button
                  variant="outline"
                  size="icon"
                  className="h-8 w-8"
                  onClick={() => handleChildrenChange(-1)}
                  disabled={children <= 0}
                >
                  -
                </Button>
                <Input
                  id="children"
                  type="number"
                  className="w-12 text-center"
                  value={children}
                  readOnly
                />
                <Button
                  variant="outline"
                  size="icon"
                  className="h-8 w-8"
                  onClick={() => handleChildrenChange(1)}
                  disabled={totalGuests >= maxGuests}
                >
                  +
                </Button>
              </div>
            </div>
          </div>
          <Button>Done</Button>
        </div>
      </PopoverContent>
    </Popover>
  );
}

